// NumberCounter.js

import React, { useEffect, useRef } from 'react';

const NumberCounter = ({ start, end, incrementSpeed, delay, runOnce }) => {
  const counterRef = useRef();
  const intervalRef = useRef();

  const increment = () => {
    let currentNumber = start;
    const incrementAmount = start < end ? 1 : -1;

    intervalRef.current = setInterval(() => {
      if ((start < end && currentNumber < end) || (start > end && currentNumber > end)) {
        currentNumber += incrementAmount;
        counterRef.current.innerText = currentNumber;
      } else {
        clearInterval(intervalRef.current);
      }
    }, incrementSpeed);
  };

  useEffect(() => {
    if (!runOnce) {
      increment();
      if (delay > 0) {
        setTimeout(() => increment(), delay);
      }
    }
  }, []);

  return (
    <div className="number-counter" ref={counterRef}>
      {start}
    </div>
  );
};

export default NumberCounter;
