// CustomBlock.js
import grapesjs from 'grapesjs';

function CustomBlockComponent(editor) {
  editor.Blocks.add('custom-block', {
    label: 'Custom Block',
    content: '<div class="custom-block">Drag me!</div>',
    category: 'Basic',
    draggable: '.custom-block',
    attributes: { class: 'custom-block' }, // Add a class for CSS customization

    // Define traits for customization
    traits: [
      { type: 'number', name: 'start', label: 'Start Number' },
      { type: 'number', name: 'end', label: 'End Number' },
      { type: 'number', name: 'speed', label: 'Counting Speed' },
      { type: 'checkbox', name: 'direction', label: 'Count Up' },
      { type: 'text', name: 'prefix', label: 'Prefix' },
      { type: 'text', name: 'suffix', label: 'Suffix' },
      { type: 'text', name: 'separator', label: 'Separator' },
    ],
  });
}

export default CustomBlockComponent;
