// NumberCounterBlock.js
import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import NumberCounterComponent from './NumberCounterComponent';

const NumberCounterBlock = () => {
  const editorRef = React.useRef(null);

  useEffect(() => {
    const editor = grapesjs.init({
      container: editorRef.current,
      // ... other GrapesJS options ...
    });

    editor.DomComponents.addType('number-counter', {
        model: {
          defaults: {
            content: 'Number Counter',
          },
        },
        view: {
          init() {
            this.listenTo(this.model, 'change:content', this.updateContent);
          },
          updateContent() {
            this.el.innerHTML = this.model.get('content');
          },
          tagName: 'div',
          attributes: { class: 'number-counter' },
        },
      });

    editor.Blocks.add('NumberCounter', {
      label: 'Number Counter',
      attributes: { class: 'fa fa-sort-numeric-asc' },
      content: {
        type: 'NumberCounter',
      },
    });
  }, []);

  return (
    <div>
      <div ref={editorRef}></div>
    </div>
  );
};

export default NumberCounterBlock;
