import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';

const NumberCounterEditor = () => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (editorRef.current) {
      const editor = grapesjs.init({
        container: editorRef.current,
        storageManager: { autoload: false }, // Disable auto-loading of blocks
      });

      // Define the Number Counter block
      editor.Blocks.add('number-counter', {
        label: 'Number Counter',
        attributes: { class: 'fa fa-clock-o' },
        content: '<div class="number-counter"><span data-start="0" data-end="100" data-direction="up" data-speed="1000">0</span></div>',
        category: 'Basic',
        // Define how the block should render
        render: ({ el }) => {
          const spanElement = el.querySelector('span');
          if (spanElement) {
            const customData = spanElement.getAttribute('data-start');
            const start = customData ? parseInt(customData) : 0;

            // Customize the rendering logic here
            let count = start;

            const interval = setInterval(() => {
              const customData = spanElement.getAttribute('data-end');
              const end = customData ? parseInt(customData) : 100;

              const customDirection = spanElement.getAttribute('data-direction');
              const direction = customDirection || 'up';

              const customSpeed = spanElement.getAttribute('data-speed');
              const speed = customSpeed ? parseInt(customSpeed) : 1000;

              if (direction === 'up' && count < end) {
                count++;
              } else if (direction === 'down' && count > end) {
                count--;
              } else {
                clearInterval(interval);
              }
              spanElement.innerText = count.toString();
            }, speed);
          }
        },
      });
    }
  }, []);

  return <div ref={editorRef} />;
};

export default NumberCounterEditor;
