// src/Editor.js
import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
// import 'grapesjs-number-counter/dist/grapesjs-number-counter.min.css';
function NumberCounterComponent(editor) {
  editor.DomComponents.addType('number-counter', {
    model: {
      defaults: {
        traits: [
          { type: 'text', name: 'start', label: 'Start Number' },
          { type: 'text', name: 'end', label: 'End Number' },
          { type: 'text', name: 'speed', label: 'Counting Speed' },
          { type: 'checkbox', name: 'direction', label: 'Count Up' },
        ],
        content: '0',
      },
    },
    view: {
      init() {
        this.listenTo(this.model, 'change:content', this.updateContent);
      },
      updateContent() {
        this.el.innerHTML = this.model.get('content');
      },

    },
  });
}

function App() {
  const editorRef = useRef(null);

  useEffect(() => {
    const editor = grapesjs.init({
      container: editorRef.current,
      components: '<div data-gjs-type="number-counter">0</div>',
      storageManager: { type: 'local' },
      // Add more GrapeJS configuration options here
    });

    NumberCounterComponent(editor);

    return () => {
      editor.destroy();
    };
  }, []);

//   return (
//     <div>
//       <div ref={editorRef}></div>
//     </div>
//   );
}

export default NumberCounterComponent;
