import React, { useEffect, useRef } from 'react';
import 'grapesjs/dist/css/grapes.min.css';
import grapesjs from 'grapesjs';

const NumberCounterEditor = () => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (editorRef.current) {
      const editor = grapesjs.init({
        container: editorRef.current,
        storageManager: { autoload: false },
      });

      editor.Blocks.add('number-counter', {
        label: 'Number Counter',
        attributes: { class: 'fa fa-clock-o' },
        content: '<div class="number-counter">0</div>',
        category: 'Basic',
        traits: [
          {
            type: 'number',
            name: 'start',
            label: 'Start Number',
            value: 0,
          },
          {
            type: 'number',
            name: 'end',
            label: 'End Number',
            value: 100,
          },
          {
            type: 'select',
            name: 'direction',
            label: 'Direction',
            options: [
              { value: 'up', name: 'Up' },
              { value: 'down', name: 'Down' },
            ],
            value: 'up',
          },
          {
            type: 'number',
            name: 'speed',
            label: 'Counting Speed (milliseconds)',
            value: 1000,
          },
        ],
        render: ({ el }) => {
          const start = el.getAttribute('data-start');
          const end = el.getAttribute('data-end');
          const direction = el.getAttribute('data-direction');
          const speed = el.getAttribute('data-speed');

          // Initialize the counter with the start value
          let count = parseInt(start);

          // Set the interval to update the counter
          const interval = setInterval(() => {
            if (direction === 'up' && count < parseInt(end)) {
              count++;
            } else if (direction === 'down' && count > parseInt(end)) {
              count--;
            } else {
              clearInterval(interval);
            }
            el.innerText = count.toString();
          }, parseInt(speed));
        },
      });

      editor.BlockManager.add('number-counter', {
        label: 'Number Counter',
        content: '',
      });
    }
  }, []);

  return <div ref={editorRef} />;
};

export default NumberCounterEditor;
