// App.js
import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
import NumberCounter from './NumberCounter';
import GrapesJSNumberCounterPlugin from './GrapesJSNumberCounterPlugin';

function App() {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      fromElement: true,
      plugins: [GrapesJSNumberCounterPlugin],
    });

    // Add your other GrapesJS configurations here

    // Define the custom components
    editor.DomComponents.addType('number-counter', {
      model: {
        defaults: {
          start: 1,          // Starting number
          end: 100,          // Ending number
          speed: 1000,       // Counting speed (milliseconds)
          direction: 'up',   // Counting direction ('up' or 'down')
        },
      },
      view: {
        init() {
          const { start, end, speed, direction } = this.model.attributes;

          // Render the Number Counter component
          this.$el.html(
            `<NumberCounter start=${start} end=${end} speed=${speed} direction=${direction} />`
          );
        },
      },
    });
  }, []);

  return (
    <div className="App">
      <div id="gjs">
        {/* GrapesJS editor will be rendered here */}
      </div>
    </div>
  );
}

export default App;
