import React, { useEffect, useRef } from 'react';
import 'grapesjs/dist/css/grapes.min.css';
import grapesjs from 'grapesjs';

const componentSettings = {
    start: {
      type: 'number',
      label: 'Start Number',
      default: 0,
    },
    end: {
      type: 'number',
      label: 'End Number',
      default: 100,
    },
    direction: {
      type: 'select',
      label: 'Direction',
      default: 'up',
      options: [
        { value: 'up', name: 'Up' },
        { value: 'down', name: 'Down' },
      ],
    },
    speed: {
      type: 'number',
      label: 'Counting Speed (milliseconds)',
      default: 1000,
    },
  };

const NumberCounterEditor = () => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (editorRef.current) {
      const editor = grapesjs.init({
        container: editorRef.current,
        storageManager: { autoload: false },
      });

      editor.Blocks.add('number-counter', {
        label: 'Number Counter',
        attributes: { class: 'fa fa-clock-o' },
        content: '<div class="number-counter">0</div>',
        category: 'Basic',
        // Define traits for component settings
        traits: [
          {
            type: 'number',
            name: 'start',
            label: 'Start Number',
            value: 0,
          },
          {
            type: 'number',
            name: 'end',
            label: 'End Number',
            value: 100,
          },
          {
            type: 'select',
            name: 'direction',
            label: 'Direction',
            options: [
              { value: 'up', name: 'Up' },
              { value: 'down', name: 'Down' },
            ],
            value: 'up',
          },
          {
            type: 'number',
            name: 'speed',
            label: 'Counting Speed (milliseconds)',
            value: 1000,
          },
        ],
        // Define render logic for the block
        render: ({ el, model }) => {
          // Extract trait values from the block model
          const start = model.get('traits').start;
          const end = model.get('traits').end;
          const direction = model.get('traits').direction;
          const speed = model.get('traits').speed;

          // Initialize and run the number counter with extracted traits
          let count = start;
          const interval = setInterval(() => {
            if (direction === 'up' && count < end) {
              count++;
            } else if (direction === 'down' && count > end) {
              count--;
            } else {
              clearInterval(interval);
            }
            el.innerHTML = count??0;
          }, speed);
        },
      });




      editor.BlockManager.add('number-counter', {
        label: 'Number Counter',
        content: '',
      });
    }
  }, []);

  return <div ref={editorRef} />;
};

export default NumberCounterEditor;
