import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import CounterNumberComponent from './CounterNumberComponent';

const CounterNumberBlock = () => {
  const editorRef = useRef(null);

  useEffect(() => {
    const editor = grapesjs.init({
      container: editorRef.current,
      // ... other GrapesJS options ...
    });

    editor.DomComponents.addType('CounterNumber', {
      model: {
        defaults: {
          type: 'CounterNumber',
          count: 0,
        },
      },
      view: CounterNumberComponent,
    });

    editor.Blocks.add('CounterNumber', {
      label: 'Number Counter',
      attributes: { class: 'fa fa-clock-o' },
      content: {
        type: 'CounterNumber',
        components: [
          {
            type: 'text',
            content: '{count}',
          },
        ],
      },
    });

    // Counting animation logic
    let count = 0;

    // Get all registered blocks
    const blockManager = editor.BlockManager;

    // Find the CounterNumber block
    let countingBlock = null;
    blockManager.getAll().forEach((block) => {
      if (block.get('label') === 'Number Counter') {
        countingBlock = block;
      }
    });

    if (countingBlock) {
      const updateCounterText = (value) => {
        const blockContent = countingBlock.get('content');
        const textComponent =1;
        textComponent.set('content', value.toString());
      };

      const countInterval = setInterval(() => {
        count++;
        if (count > 100) {
          clearInterval(countInterval);
        }
        updateCounterText(count);
      }, 1000);

      return () => {
        clearInterval(countInterval);
      };
    } else {
      console.error('CounterNumber block not found');
    }
  }, []);

  return (
    <div>
      <div ref={editorRef}></div>
    </div>
  );
};

export default CounterNumberBlock;
