import React, { useEffect, useRef } from 'react';
import 'grapesjs/dist/css/grapes.min.css';
import grapesjs from 'grapesjs';

const NumberCounterEditor = () => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (editorRef.current) {
      const editor = grapesjs.init({
        container: editorRef.current,
        storageManager: { autoload: false },
      });

      editor.Blocks.add('number-counter', {
        label: 'Number Counter',
        attributes: { class: 'fa fa-clock-o' },
        content: {
          type: 'number-counter',
          components: [
            {
              tagName: 'div',
              attributes: {
                class: 'number-counter',
                'data-start': '0',
                'data-end': '100',
                'data-direction': 'up',
                'data-speed': '1000',
              },
              components: [
                {
                  type: 'text',
                  content: '0',
                },
              ],
            },
          ],
        },
        category: 'Basic',
        // Add Component settings as traits
        traits: [
          {
            type: 'number',
            name: 'start',
            label: 'Start Number',
            value: 0,
          },
          {
            type: 'number',
            name: 'end',
            label: 'End Number',
            value: 100,
          },
          {
            type: 'select',
            name: 'direction',
            label: 'Direction',
            options: [
              { value: 'up', name: 'Up' },
              { value: 'down', name: 'Down' },
            ],
            value: 'up',
          },
          {
            type: 'number',
            name: 'speed',
            label: 'Counting Speed (milliseconds)',
            value: 1000,
          },
        ],
      });



      editor.BlockManager.add('number-counter', {
        label: 'Number Counter',
        content: '',
      });
    }
  }, []);

  return <div ref={editorRef} />;
};

export default NumberCounterEditor;
