// NumberCounter.js
import React, { useState, useEffect } from 'react';

const NumberCounter = ({ start, end, speed, direction }) => {
  const [count, setCount] = useState(start);

  useEffect(() => {
    const interval = setInterval(() => {
      if (direction === 'up') {
        if (count < end) {
          setCount(count + 1);
        }
      } else if (direction === 'down') {
        if (count > end) {
          setCount(count - 1);
        }
      }
    }, speed);

    return () => clearInterval(interval);
  }, [count, start, end, speed, direction]);

  return <div className="number-counter">{count}</div>;
};

export default NumberCounter;
