import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
// GrapesJSEditor.js
// import React, { useEffect } from 'react';
// import grapesjs from 'grapesjs';
// import 'grapesjs-blocks-basic';

const GrapesJSEditor = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      fromElement: true,
      storageManager: {
        type: 'local',
      },
      plugins: ['gjs-blocks-basic'],
    });

    // Define the NumberCounter component
    editor.DomComponents.addType('number-counter', {
      model: {
        defaults: {
          tagName: 'div',
          attributes: { class: 'number-counter' },
        },
      },
      init() {
        this.addAttributes({
          start: { type: 'number', default: 0 },
          end: { type: 'number', default: 100 },
          speed: { type: 'number', default: 1000 },
          direction: { type: 'select', options: ['up', 'down'], default: 'up' },
        });
        this.updateContent();
      },
      updateContent() {
        const { start, end, speed, direction } = this.getAttributes();
        // Your logic to update the content based on start, end, speed, and direction
      },
    });

    // Add a block for the NumberCounter component
    editor.BlockManager.add('number-counter-block', {
      label: 'NumberCounter',
      attributes: { class: 'fa fa-clock-o' },
      content: '<div data-gjs-type="number-counter" data-gjs-draggable="true">0</div>',
    });

    // Add an asset to the Asset Manager for the NumberCounter component
    editor.AssetManager.add('number-counter-asset', {
      label: 'NumberCounter',
      content: '<div data-gjs-type="number-counter" data-gjs-draggable="true">0</div>',
      category: 'Components',
      attributes: {
        start: 0,
        end: 100,
        speed: 1000,
        direction: 'up',
      },
    });
  }, []);

  return (
    <div>
      <div id="gjs" style={{ height: '500px' }}></div>
    </div>
  );
};

export default GrapesJSEditor;


