import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';


const GrapesJSEditor: React.FC = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      fromElement: true,
      storageManager: {
        autosave: true,
        type: 'local',
        stepsBeforeSave: 1,
      },
      plugins: ['gjs-blocks-basic'], // Example: Basic Blocks plugin
      blockManager: {
        appendTo: '#blocks',
        blocks: [
          {
            id: 'number-counter',
            label: 'NumberCounter',
            attributes: { class: 'fa fa-clock-o' },
            content: '<div data-gjs-type="number-counter">0</div>',
          },
        ],
      },
    });

    // Define the NumberCounter component and its logic
    editor.DomComponents.addType('number-counter', {
      model: {
        defaults: {
          tagName: 'div',
          content: '0',
          attributes: { class: 'number-counter' },
          start: 0,
          end: 100,
          speed: 1000,
          direction: 'up',
        },
      },
      init() {
        this.updateContent();
      },
      updated(prop, value) {
        if (prop === 'start' || prop === 'end' || prop === 'speed' || prop === 'direction') {
          this.updateContent();
        }
      },
      updateContent() {
        const { start, end, speed, direction } = this.get('attributes');
        this.set('content', String(start));

        let current = start;
        const increment = direction === 'up' ? 1 : -1;
        const interval = speed / Math.abs(end - start);

        const update = () => {
          current += increment;
          this.set('content', String(current));
          if ((direction === 'up' && current <= end) || (direction === 'down' && current >= end)) {
            setTimeout(update, interval);
          }
        };

        setTimeout(update, interval);
      },
    });

    // Set traits for the NumberCounter component
    editor.TraitManager.addType('number-counter-options', {
      noValue: true,
      createInput: function (el) {
        const input = document.createElement('input');
        input.setAttribute('placeholder', 'Enter a value');
        input.value = el.innerHTML;
        return input;
      },
      onUpdate: function (el, value) {
        el.innerHTML = value;
      },
    });

    // Add a blank canvas for users to start working on
    editor.setComponents('<div data-gjs-type="number-counter">0</div>');
  }, []);

  return (
    <div>
      <div id="gjs" style={{ height: '500px' }}></div>
      <div id="blocks" style={{ display: 'none' }}></div>
    </div>
  );
};

export default GrapesJSEditor;

