import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
import React, { useEffect, useRef, useState } from 'react';

function GrapesJsEditor() {
  const editorRef = useRef(null);
  const [counterOptions, setCounterOptions] = useState({
    startNumber: 1,
    endNumber: 100,
    countSpeed: 1000,
    countDirection: 'up',
    prefix: '',
    suffix: '',
    separator: '',
  });

  useEffect(() => {
    const editor = grapesjs.init({
      container: '#grapesjs-container',
      storageManager: false, // Disable storage for simplicity
      // Your GrapesJS configuration options here
    });

    // Define the custom Number Counter component
    editor.Components.addType('number-counter', {
      model: {
        defaults: {
          traits: [
            {
              type: 'number',
              name: 'startNumber',
              label: 'Start Number',
              changeProp: true,
            },
            {
              type: 'number',
              name: 'endNumber',
              label: 'End Number',
              changeProp: true,
            },
            {
              type: 'number',
              name: 'countSpeed',
              label: 'Count Speed (ms)',
              changeProp: true,
            },
            {
              type: 'select',
              name: 'countDirection',
              label: 'Count Direction',
              options: [
                { value: 'up', name: 'Up' },
                { value: 'down', name: 'Down' },
              ],
              changeProp: true,
            },
            {
              type: 'text',
              name: 'prefix',
              label: 'Prefix',
              changeProp: true,
            },
            {
              type: 'text',
              name: 'suffix',
              label: 'Suffix',
              changeProp: true,
            },
            {
              type: 'text',
              name: 'separator',
              label: 'Separator',
              changeProp: true,
            },
          ],
        },
      },
      view: {},
    });

    // Register a command to add the Number Counter block
    editor.Commands.add('add-number-counter', () => {
      const block = editor.BlockManager.get('number-counter');
      const section = editor.DomComponents.get();
      section.get('components').add(block.attributes.content.components);
      editor.get('DomComponents').addComponent(section);
    });

    // Add a button to the GrapesJS toolbar to insert the Number Counter block
    editor.Panels.addButton('options', [
      {
        id: 'add-number-counter',
        className: 'fa fa-sort-numeric-asc',
        command: 'add-number-counter',
        attributes: { title: 'Add Number Counter' },
      },
    ]);

    editorRef.current = editor;
  }, []);

  useEffect(() => {
    // Update the Number Counter options when they change
    const editor = editorRef.current;
    const component = editor.getSelected();
    if (component && component.attributes.type === 'number-counter') {
      const { startNumber, endNumber, countSpeed, countDirection, prefix, suffix, separator } = counterOptions;
      component.set('traits', {
        startNumber,
        endNumber,
        countSpeed,
        countDirection,
        prefix,
        suffix,
        separator,
      });
      component.trigger('change:traits');
    }
  }, [counterOptions]);

  return (
    <div>
      <div>
        <label>Start Number:</label>
        <input
          type="number"
          value={counterOptions.startNumber}
          onChange={(e) => setCounterOptions({ ...counterOptions, startNumber: parseInt(e.target.value) })}
        />
      </div>
      <div>
        <label>End Number:</label>
        <input
          type="number"
          value={counterOptions.endNumber}
          onChange={(e) => setCounterOptions({ ...counterOptions, endNumber: parseInt(e.target.value) })}
        />
      </div>
      <div>
        <label>Count Speed (ms):</label>
        <input
          type="number"
          value={counterOptions.countSpeed}
          onChange={(e) => setCounterOptions({ ...counterOptions, countSpeed: parseInt(e.target.value) })}
        />
      </div>
      <div>
        <label>Count Direction:</label>
        <select
          value={counterOptions.countDirection}
          onChange={(e) => setCounterOptions({ ...counterOptions, countDirection: e.target.value })}
        >
          <option value="up">Up</option>
          <option value="down">Down</option>
        </select>
      </div>
      <div>
        <label>Prefix:</label>
        <input
          type="text"
          value={counterOptions.prefix}
          onChange={(e) => setCounterOptions({ ...counterOptions, prefix: e.target.value })}
        />
      </div>
      <div>
        <label>Suffix:</label>
        <input
          type="text"
          value={counterOptions.suffix}
          onChange={(e) => setCounterOptions({ ...counterOptions, suffix: e.target.value })}
        />
      </div>
      <div>
        <label>Separator:</label>
        <input
          type="text"
          value={counterOptions.separator}
          onChange={(e) => setCounterOptions({ ...counterOptions, separator: e.target.value })}
        />
      </div>
      <div id="grapesjs-container" />
    </div>
  );
}

