function CustomBlockComponent(editor) {
  editor.Blocks.add('custom-block', {
    label: 'Custom Block',
    content: '<div class="custom-block">0</div>', // Set initial content to '0'
    category: 'Basic',
    draggable: '.custom-block',
    attributes: { class: 'custom-block' }, // Add a class for CSS customization

    // Define traits for customization with initial values
    traits: [
      { type: 'number', name: 'start', label: 'Start Number', value: 0 }, // Set initial value to 0
      { type: 'number', name: 'end', label: 'End Number', value: 100 }, // Set initial value to 100
      { type: 'number', name: 'speed', label: 'Counting Speed', value: 1000 }, // Set initial value to 1000 (milliseconds)
      { type: 'checkbox', name: 'direction', label: 'Count Up', valueTrue: true, valueFalse: false }, // Set initial value to true (Count Up)
      { type: 'text', name: 'prefix', label: 'Prefix' },
      { type: 'text', name: 'suffix', label: 'Suffix' },
      { type: 'text', name: 'separator', label: 'Separator' },
    ],
  });

  // Run the counter logic after the block is added or modified
  editor.on('block:add', (block) => {
    console.log('block:drag:stop')
    if (block.get('name') === 'custom-block') {
      // Access trait values correctly using block.getTrait method
      const start = block.getTrait('start');
      const end = block.getTrait('end');
      const speed = block.getTrait('speed');
      const direction = block.getTrait('direction');
    //   const prefix = block.getTrait('prefix');
    //   const suffix = block.getTrait('suffix');
    //   const separator = block.getTrait('separator');

      let currentNumber = start;
      let increment = 1;

      if (!direction) {
        currentNumber = end;
        increment = -1;
      }

      // Function to update the content
      function updateContent() {
        console.log(currentNumber)
        if ((direction && currentNumber <= end) || (!direction && currentNumber >= start)) {
            console.log(currentNumber)
          // Update the block content and continue the counter if not reached the end or start
          block.set('content', currentNumber);
          block.trigger('change:content');
          currentNumber += increment;
          setTimeout(updateContent, speed);
        }
      }

      // Start the counter
      updateContent();
    }
  });
}

export default CustomBlockComponent;
