import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';

const GrapesJSEditor = () => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (editorRef.current) {
      const editor = grapesjs.init({
        container: editorRef.current,
        storageManager: { autoload: false }, // Disable auto-loading of blocks
      });

      // Define the Number Counter block
      editor.Blocks.add('number-counter', {
        label: 'Number Counter',
        attributes: { class: 'fa fa-clock-o' },
        content: '<div class="number-counter" data-start="0" data-end="100" data-direction="up" data-speed="1000">0</div>',
        category: 'Basic',
        render: ({ el }) => {
          let count = parseInt(el.getAttribute('data-start'));
          const end = parseInt(el.getAttribute('data-end'));
          const direction = el.getAttribute('data-direction');
          const speed = parseInt(el.getAttribute('data-speed'));
          const interval = setInterval(() => {
            if ((direction === 'up' && count < end) || (direction === 'down' && count > end)) {
              el.innerText = count.toString();
              count = direction === 'up' ? count + 1 : count - 1;
            } else {
              clearInterval(interval);
            }
          }, speed);
        },
      });

      // Add customization options to the panel
      editor.Panels.addPanel({
        id: 'number-counter-panel',
        visible: true,
        buttons: [
          {
            id: 'set-start-end',
            label: 'Set Start and End',
            command: 'set-start-end',
            context: 'number-counter',
          },
          {
            id: 'set-direction',
            label: 'Set Direction',
            command: 'set-direction',
            context: 'number-counter',
          },
          {
            id: 'set-speed',
            label: 'Set Speed',
            command: 'set-speed',
            context: 'number-counter',
          },
        ],
      });

      // Add commands for customization
      editor.Commands.add('set-start-end', {
        run: (editor, sender) => {
          const selected = editor.getSelected();
          if (selected) {
            const start = prompt('Enter Start Number:', selected.getAttribute('data-start'));
            const end = prompt('Enter End Number:', selected.getAttribute('data-end'));
            if (start !== null && end !== null) {
              selected.setAttribute('data-start', start);
              selected.setAttribute('data-end', end);
              editor.Modal.close();
            }
          }
        },
      });

      editor.Commands.add('set-direction', {
        run: (editor, sender) => {
          const selected = editor.getSelected();
          if (selected) {
            const direction = prompt('Enter Direction (up or down):', selected.getAttribute('data-direction'));
            if (direction !== null && (direction === 'up' || direction === 'down')) {
              selected.setAttribute('data-direction', direction);
              editor.Modal.close();
            }
          }
        },
      });

      editor.Commands.add('set-speed', {
        run: (editor, sender) => {
          const selected = editor.getSelected();
          if (selected) {
            const speed = prompt('Enter Speed (milliseconds):', selected.getAttribute('data-speed'));
            if (speed !== null) {
              selected.setAttribute('data-speed', speed);
              editor.Modal.close();
            }
          }
        },
      });
    }
  }, []);

  return <div ref={editorRef}></div>;
};

export default GrapesJSEditor;
