import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';

const GrapesJSEditor: React.FC = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      // Configuration options for GrapesJS
      components: [
        '<div data-gjs-type="number-counter">0</div>',
      ],
      plugins: [],
    });

    editor.DomComponents.addType('number-counter', {
      model: {
        defaults: {
          tagName: 'div',
          content: '0',
          attributes: { class: 'number-counter' },
          start: 0,
          end: 100,
          speed: 1000,
          direction: 'up',
        },
      },
      init() {
        this.updateContent();
      },
      updated(prop, value) {
        if (prop === 'start' || prop === 'end' || prop === 'speed' || prop === 'direction') {
          this.updateContent();
        }
      },
      updateContent() {
        const { start, end, speed, direction } = this.get('attributes');
        this.set('content', String(start));

        let current = start;
        const increment = direction === 'up' ? 1 : -1;
        const interval = speed / Math.abs(end - start);

        const update = () => {
          current += increment;
          this.set('content', String(current));
          if ((direction === 'up' && current <= end) || (direction === 'down' && current >= end)) {
            setTimeout(update, interval);
          }
        };

        setTimeout(update, interval);
      },
    });
  }, []);

  return <div id="gjs" />;
};

export default GrapesJSEditor;
