// src/NumberCounter.js
import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';

function NumberCounterComponent(editor) {
  editor.DomComponents.addType('number-counter', {
    model: {
      defaults: {
        traits: [
          { type: 'number', name: 'start', label: 'Start Number' },
          { type: 'number', name: 'end', label: 'End Number' },
          { type: 'number', name: 'speed', label: 'Counting Speed' },
          { type: 'checkbox', name: 'direction', label: 'Count Up' },
          { type: 'text', name: 'prefix', label: 'Prefix' },
          { type: 'text', name: 'suffix', label: 'Suffix' },
          { type: 'text', name: 'separator', label: 'Separator' },
        ],
        content: '0',
      },
    },
    view: {
      onRender() {
        const model = this.model;
        const start = model.get('start');
        const end = model.get('end');
        const speed = model.get('speed');
        const direction = model.get('direction');
        const prefix = model.get('prefix');
        const suffix = model.get('suffix');
        const separator = model.get('separator');
        let currentNumber = start;
        let increment = 1;

        if (!direction) {
          currentNumber = end;
          increment = -1;
        }

        setInterval(() => {
          if ((direction && currentNumber >= end) || (!direction && currentNumber <= start)) {
            return;
          }

          this.el.innerHTML = prefix + currentNumber + suffix;
          currentNumber += increment;
        }, speed);
      },
    },
  });
}

function App() {
  const editorRef = useRef(null);

  useEffect(() => {
    const editor = grapesjs.init({
      container: editorRef.current,
      storageManager: { type: 'local' },
      // Add more GrapeJS configuration options here
    });

    NumberCounterComponent(editor);

    return () => {
      editor.destroy();
    };
  }, []);

  return (
    <div>
      <div ref={editorRef}></div>
    </div>
  );
}

export default NumberCounterComponent;
