// GrapesJSNumberCounterPlugin.js
export default (editor, opts = {}) => {
    // Add the Number Counter component to the GrapesJS blocks
    editor.Blocks.add('number-counter', {
      label: 'Number Counter',
      content: '<div data-gjs-type="number-counter"></div>',
    });

    // Define the component for the Number Counter element
    editor.Components.addType('number-counter', {
      model: {
        defaults: {
          start: 1,          // Starting number
          end: 100,          // Ending number
          speed: 1000,       // Counting speed (milliseconds)
          direction: 'up',   // Counting direction ('up' or 'down')
        },
      },
      view: {
        init() {
          const { start, end, speed, direction } = this.model.attributes;

          // Render the Number Counter component
          this.$el.html(
            `<NumberCounter start=${start} end=${end} speed=${speed} direction=${direction} />`
          );
        },
      },
    });
  };
