// src/Editor.js
import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
import 'grapesjs-number-counter/dist/grapesjs-number-counter.min.css';
import 'grapesjs-number-counter';

function NumberCounterEditor() {
  const editorRef = useRef(null);

  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      fromElement: true,
      storageManager: false,
      plugins: ['gjs-number-counter'],
      pluginsOpts: {
        'gjs-number-counter': {
          start: 0,             // Start value of the counter
          end: 100,             // End value of the counter
          prefix: '',           // Prefix to display before the number
          suffix: '',           // Suffix to display after the number
          duration: 2000,       // Duration of the counting animation in milliseconds
          easing: 'linear',     // Easing function for the animation (e.g., 'linear', 'ease-in', 'ease-out')
        },
      },
    });

    // Add a number counter element
    editor.BlockManager.add('number-counter', {
      label: 'Number Counter',
      attributes: { class: 'fa fa-sort-numeric-asc' },
      content: '<div data-gjs-type="gjs-number-counter"></div>',
    });

    editorRef.current = editor;
  }, []);

  // Function to start the counter animation
  const startCounter = () => {
    if (editorRef.current) {
      const counterBlock = editorRef.current.getSelected();
      if (counterBlock) {
        const numberCounter = counterBlock.components().at(0);
        if (numberCounter) {
          numberCounter.start();
        }
      }
    }
  };

  return (
    <div>
      <div id="gjs" style={{ height: '400px', width: '100%' }}></div>
      <button onClick={startCounter}>Start Counter</button>
    </div>
  );
}

export default NumberCounterEditor;
