import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
// Define the custom Number Counter block for GrapesJS
editor.Blocks.add('number-counter', {
    label: 'Number Counter',
    category: 'Basic',
    attributes: { class: 'fa fa-sort-numeric-asc' },
    content: {
      type: 'number-counter',
      components: [
        {
          type: 'text',
          content: '0', // Default starting number
          style: {
            'font-size': '36px',
            'font-weight': 'bold',
          },
        },
      ],
    },
  });

  // Define the custom Number Counter component for GrapesJS
  editor.Components.addType('number-counter', {
    model: {
      defaults: {
        traits: [
          // You can add customization traits here (e.g., speed, direction).
        ],
      },
    },
    view: {},
  });

  // Register the custom Number Counter component with GrapesJS
  editor.Commands.add('add-number-counter', {
    run: (editor) => {
      editor.BlockManager.get('number-counter').render();
    },
  });

  // Add a button to the GrapesJS toolbar to insert the Number Counter block
  editor.Panels.addButton('options', [
    {
      id: 'add-number-counter',
      className: 'fa fa-sort-numeric-asc',
      command: 'add-number-counter',
      attributes: { title: 'Add Number Counter' },
    },
  ]);

  // Initialize GrapesJS
  editor.init({
    // Your GrapesJS configuration options here
  });
