// src/GrapeJS.js
import grapesjs from "grapesjs";
import { CustomComponent } from "./CustomComponent";

export const initGrapeJS = () => {
  const editor = grapesjs.init({
    container: "#gjs",
    // Add other GrapeJS configuration options here
  });

  editor.Commands.add("add-counter", {
    run(editor) {
      editor.Panels.getPanel("components").get("views-container").appendChild(
        CustomComponent(editor, {
          text: "Counter: ",
          start: 0,
          end: 100,
          speed: 1,
        })
      );
    },
  });
};
