import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';

function NumberCounterBlock() {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      fromElement: true,
      storageManager: { autoload: 0 },
    });

    // Define a custom block
    editor.Blocks.add('number-counter', {
      label: 'Number Counter',
      content: '<div id="counter">0</div>',
      attributes: { class: 'number-counter' },
      render: ({ el }) => {
        // Increment the counter from 0 to 100
        let count = 0;
        const counterElement = el.querySelector('#counter');

        function updateCounter() {
          if (counterElement && count < 100) {
            count++;
            counterElement.textContent = count;
            requestAnimationFrame(updateCounter);
          }
        }

        // Ensure the counter updates only after the element is in the DOM
        counterElement && updateCounter();
      },
    });
  }, []);

  return <div id="gjs" />;
}

export default NumberCounterBlock;
