// NumberCounterComponent.js
import React from 'react';
import 'grapesjs/dist/css/grapes.min.css';

const NumberCounterComponent = ({ start, end, speed }) => {
  const [count, setCount] = React.useState(start);

  React.useEffect(() => {
    let interval;
    if (start < end) {
      interval = setInterval(() => {
        if (count < end) {
          setCount(count + 1);
        }
      }, speed);
    } else if (start > end) {
      interval = setInterval(() => {
        if (count > end) {
          setCount(count - 1);
        }
      }, speed);
    }

    return () => clearInterval(interval);
  }, [start, end, speed, count]);

  return (
    <div>
      <p>Count: {count}</p>
    </div>
  );
};

export default NumberCounterComponent;
