import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
// GrapesJSEditor.tsx
// import React, { useEffect } from 'react';
// import grapesjs from 'grapesjs';

const GrapesJSEditor: React.FC = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      fromElement: true,
      storageManager: {
        type: 'local',
        stepsBeforeSave: 1,
      },
      blockManager: {
        appendTo: '#blocks',
      },
    });

    // Define the NumberCounter component
    editor.Blocks.add('number-counter', {
      label: 'NumberCounter',
      content: '<div data-gjs-type="number-counter" data-gjs-draggable="true">0</div>',
    });

    // Define the NumberCounter traits
    editor.Traits.add('number-counter-traits', {
      start: {
        type: 'number',
        label: 'Start Value',
      },
      end: {
        type: 'number',
        label: 'End Value',
      },
      speed: {
        type: 'number',
        label: 'Speed (ms)',
      },
      direction: {
        type: 'select',
        options: [
          { value: 'up', name: 'Up' },
          { value: 'down', name: 'Down' },
        ],
        label: 'Direction',
      },
    });

    // Handle the event after a component is dragged and dropped
    editor.on('component:drag:end', (component) => {
      if (component.is('number-counter')) {
        // Add your logic to set up the counter component here
        const start = component.getTrait('start');
        const end = component.getTrait('end');
        const speed = component.getTrait('speed');
        const direction = component.getTrait('direction');

        // Your logic to set up the counter based on traits
        console.log('Start:', start, 'End:', end, 'Speed:', speed, 'Direction:', direction);
      }
    });
  }, []);

  return (
    <div>
      <div id="gjs" style={{ height: '500px' }}></div>
      <div id="blocks" style={{ display: 'none' }}></div>
    </div>
  );
};

export default GrapesJSEditor;


