import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';

// Define a React component that wraps the GrapesJS editor
function GrapesJsEditor() {
  const editorRef = useRef(null);

  useEffect(() => {
    // Initialize GrapesJS
    const editor = grapesjs.init({
      container: '#grapesjs-container',
      storageManager: false, // Disable storage for simplicity
      // Your GrapesJS configuration options here
    });

    // Register the Number Counter block
    editor.BlockManager.add('number-counter', {
      label: 'Number Counter',
      category: 'Basic',
      content: {
        type: 'number-counter',
        components: [
          {
            type: 'text',
            content: '0', // Default starting number
            style: {
              'font-size': '36px',
              'font-weight': 'bold',
            },
          },
        ],
      },
    });

    // Define the custom Number Counter component
    editor.Components.addType('number-counter', {
      model: {
        defaults: {
          traits: [
            {
              type: 'number',
              name: 'startNumber',
              label: 'Start Number',
              changeProp: true,
            },
            {
              type: 'number',
              name: 'endNumber',
              label: 'End Number',
              changeProp: true,
            },
            {
              type: 'number',
              name: 'countSpeed',
              label: 'Count Speed (ms)',
              changeProp: true,
            },
            {
              type: 'select',
              name: 'countDirection',
              label: 'Count Direction',
              options: [
                { id: 'up', name: 'Up' },
                { id: 'down', name: 'Down' },
              ],
              changeProp: true,
            },
            {
              type: 'text',
              name: 'prefix',
              label: 'Prefix',
              changeProp: true,
            },
            {
              type: 'text',
              name: 'suffix',
              label: 'Suffix',
              changeProp: true,
            },
            {
              type: 'text',
              name: 'separator',
              label: 'Separator',
              changeProp: true,
            },
          ],
        },
      },
      view: {},
    });

    // Register a command to add the Number Counter block
    editor.Commands.add('add-number-counter', () => {
      const block = editor.BlockManager.get('number-counter');
      block.render();
    });

    // Add a button to the GrapesJS toolbar to insert the Number Counter block
    editor.Panels.addButton('options', [
      {
        id: 'add-number-counter',
        className: 'fa fa-sort-numeric-asc',
        command: 'add-number-counter',
        attributes: { title: 'Add Number Counter' },
      },
    ]);

    // Save the editor instance to the ref for further interactions
    editorRef.current = editor;
  }, []);

  return (
    <div>
      <div id="grapesjs-container" />
    </div>
  );
}

// Your main React application component
// function App() {
