import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import NumberCounterComponent from './NumberCounter'; // Import your NumberCounter component

function App() {
  const editorRef = useRef(null);

  useEffect(() => {
    const editor = grapesjs.init({
      container: editorRef.current,
      storageManager: { type: 'local' },
      // Add more GrapeJS configuration options here
    });

    NumberCounterComponent(editor); // Add your NumberCounter component to the editor

    return () => {
      editor.destroy();
    };
  }, []);

  return (
    <div>
      <div ref={editorRef}></div>
    </div>
  );
}

export default App;
