import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';

const GrapesJSEditor: React.FC = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      fromElement: true, // Enables GrapesJS to build the editor from the HTML container
      plugins: [],
      storageManager: false, // Disable storage manager for simplicity
      blockManager: {
        // Define the NumberCounter component as a block
        appendTo: '#blocks',
        blocks: [
          {
            id: 'number-counter',
            label: 'NumberCounter',
            content: {
              type: 'number-counter',
              start: 0,
              end: 100,
              speed: 1000,
              direction: 'up',
            },
          },
        ],
      },
    });

    editor.DomComponents.addType('number-counter', {
      model: {
        defaults: {
          tagName: 'div',
          content: '0',
          attributes: { class: 'number-counter' },
        },
      },
      init() {
        this.updateContent();
      },
      updated(prop, value) {
        if (prop === 'start' || prop === 'end' || prop === 'speed' || prop === 'direction') {
          this.updateContent();
        }
      },
      updateContent() {
        const { start, end, speed, direction } = this.get('attributes');
        this.set('content', String(start));

        let current = start;
        const increment = direction === 'up' ? 1 : -1;
        const interval = speed / Math.abs(end - start);

        const update = () => {
          current += increment;
          this.set('content', String(current));
          if ((direction === 'up' && current <= end) || (direction === 'down' && current >= end)) {
            setTimeout(update, interval);
          }
        };

        setTimeout(update, interval);
      },
    });

    // Add a blank canvas for users to start working on
    editor.setComponents('<div data-gjs-type="number-counter">0</div>');
  }, []);

  return (
    <div>
      <div id="gjs" style={{ height: '500px' }}></div>
      <div id="blocks" style={{ display: 'none' }}></div>
    </div>
  );
};

export default GrapesJSEditor;
