import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';


const GrapesJSEditor: React.FC = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      // Configuration options for GrapesJS
      components: [
        {
          // Add a block that includes the NumberCounter component
          type: 'number-counter',
          components: [
            {
              type: 'number-counter',
              start: 0,
              end: 100,
              speed: 1000,
              direction: 'up',
            },
          ],
        },
      ],
      plugins: [],
    });

    editor.DomComponents.addType('number-counter', {
      model: {
        defaults: {
          tagName: 'div',
          content: 'NumberCounter',
          attributes: { class: 'number-counter' },
        },
      },
    });
  }, []);

  return <div id="gjs" />;
};

export default GrapesJSEditor;
