import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';
// GrapesJSEditor.js
// import React, { useEffect } from 'react';
// import grapesjs from 'grapesjs';
// import 'grapesjs-blocks-basic';

const GrapesJSEditor = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      fromElement: true,
      storageManager: {
        type: 'local',
      },
      plugins: ['gjs-blocks-basic'],
    });

    // Define the NumberCounter component
    editor.DomComponents.addType('number-counter', {
      model: {
        defaults: {
          tagName: 'div',
          attributes: { class: 'number-counter' },
          start: 0,
          end: 100,
          speed: 1000,
          direction: 'up',
        },
        init() {
          this.updateContent();
        },
        updated(prop, value) {
          if (prop === 'start' || prop === 'end' || prop === 'speed' || prop === 'direction') {
            this.updateContent();
          }
        },
        updateContent() {
          const { start, end, speed, direction } = this.get('attributes');
          this.set('content', String(start));

          let current = start;
          const increment = direction === 'up' ? 1 : -1;
          const interval = speed / Math.abs(end - start);

          const update = () => {
            current += increment;
            this.set('content', String(current));
            if ((direction === 'up' && current <= end) || (direction === 'down' && current >= end)) {
              setTimeout(update, interval);
            }
          };

          setTimeout(update, interval);
        },
      },
      view: {
        onRender() {
          this.updateContent(); // Render content on initial load
        },
      },
    });

    // Add a block for the NumberCounter component
    editor.BlockManager.add('number-counter-block', {
      label: 'NumberCounter',
      attributes: { class: 'fa fa-clock-o' },
      content: '<div data-gjs-type="number-counter" data-gjs-draggable="true">0</div>',
    });

    // Set traits for the NumberCounter component
    editor.on('component:selected', (component) => {
      if (component.is('number-counter')) {
        editor.TraitManager.addType('start', {
          type: 'number',
          name: 'Start Value',
          changeProp: 1,
        });
        editor.TraitManager.addType('end', {
          type: 'number',
          name: 'End Value',
          changeProp: 1,
        });
        editor.TraitManager.addType('speed', {
          type: 'number',
          name: 'Speed',
          changeProp: 1,
        });
        editor.TraitManager.addType('direction', {
          type: 'select',
          name: 'Direction',
          options: [
            { value: 'up', name: 'Up' },
            { value: 'down', name: 'Down' },
          ],
          changeProp: 1,
        });
      }
    });
  }, []);

  return (
    <div>
      <div id="gjs" style={{ height: '500px' }}></div>
    </div>
  );
};

export default GrapesJSEditor;


