import React, { useEffect, useRef } from 'react';
import grapesjs from 'grapesjs';
import 'grapesjs/dist/css/grapes.min.css';

const NumberCounterEditor = () => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (editorRef.current) {
      const editor = grapesjs.init({
        container: editorRef.current,
        storageManager: { autoload: false },
        // Add any other configuration options as needed
      });

      // Define the Number Counter block
      editor.Blocks.add('number-counter', {
        label: 'Number Counter',
        attributes: { class: 'fa fa-clock-o' },
        content: '<div class="number-counter">0</div>',
        category: 'Basic',
        render: ({ el }) => {
          const customData = el.getAttribute('data-custom-data');
          if (customData) {
            const { start, end, direction, speed } = JSON.parse(customData);
            let count = start;

            const interval = setInterval(() => {
              if (direction === 'up' && count < end) {
                count++;
              } else if (direction === 'down' && count > end) {
                count--;
              } else {
                clearInterval(interval);
              }
              el.innerText = count.toString();
            }, speed);
          }
        },
      });

      // Define Component Settings for the Number Counter block
      editor.TraitManager.addType('number-counter-props', {
        name: 'Start',
        label: 'Start Number',
        type: 'number',
        changeProp: 1,
      });

      editor.TraitManager.addType('number-counter-props', {
        name: 'End',
        label: 'End Number',
        type: 'number',
        changeProp: 1,
      });

      editor.TraitManager.addType('number-counter-props', {
        name: 'Direction',
        label: 'Direction (up or down)',
        type: 'select',
        options: [
          { value: 'up', name: 'Up' },
          { value: 'down', name: 'Down' },
        ],
        changeProp: 1,
      });

      editor.TraitManager.addType('number-counter-props', {
        name: 'Speed',
        label: 'Speed (milliseconds)',
        type: 'number',
        changeProp: 1,
      });

      // Add Component Settings panel for the Number Counter block
      editor.Panels.addPanel({
        id: 'number-counter-props-panel',
        visible: false,
        buttons: [
          {
            id: 'open-props',
            active: true,
            label: 'Properties',
            command: 'show-props',
            context: 'number-counter',
          },
        ],
      });

      // Add a command to show the Component Settings panel
      editor.Commands.add('show-props', {
        run(editor, sender) {
          editor.Panels.getPanel('number-counter-props-panel').set('active', 1);
        },
      });
    }
  }, []);

  return <div ref={editorRef} />;
};

export default NumberCounterEditor;
