// App.js

import React from 'react';
import ReactDOM from 'react-dom';
import grapesjs from 'grapesjs';
import gjsReact from 'grapesjs-react';
import NumberCounter from './NumberCounter';

// Initialize GrapesJS with React integration
grapesjs.plugins.add('gjs-react', gjsReact);

const App = () => {
  // Create a container for the GrapesJS editor
  const editorContainerRef = React.useRef(null);

  // Define GrapesJS configurations
  const editorConfig = {
    container: editorContainerRef.current,
    fromElement: true,
    plugins: ['gjs-react'],
    // Other GrapesJS configurations here
  };

  // Register the custom Number Counter component
  grapesjs.plugins.add('number-counter', (editor) => {
    editor.DomComponents.addType('number-counter', {
      model: {
        defaults: {
          content: '0', // Initial content can be any number
        },
      },
      view: {
        component: NumberCounter,
      },
    });
  });

  // Initialize the GrapesJS editor
  const editor = grapesjs.init(editorConfig);

  // Load your GrapesJS template or setup as needed

  return (
    <div>
      <h1>GrapesJS Number Counter Example</h1>
      <div ref={editorContainerRef}></div>
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
