import React, { useEffect } from 'react';
import grapesjs from 'grapesjs';
import CounterNumberComponent from './CounterNumberComponent';

const CounterNumberBlock = () => {
  useEffect(() => {
    const editor = grapesjs.init({
      container: '#gjs',
      // ... other GrapesJS options ...
    });

    editor.DomComponents.addType('CounterNumber', {
      model: {
        defaults: {
          type: 'CounterNumber',
          count: 0,
        },
      },
      view: CounterNumberComponent,
    });

    editor.Blocks.add('CounterNumber', {
      label: 'Number Counter',
      attributes: { class: 'fa fa-clock-o' },
      content: {
        type: 'CounterNumber',
        components: [
          {
            type: 'text',
            content: '{count}',
          },
        ],
      },
    });

    // Counting animation logic
    let count = 0;
    const countingBlock = editor.BlockManager.get('CounterNumber');

    const countInterval = setInterval(() => {
      count++;
      if (count > 100) {
        clearInterval(countInterval);
      }
      const block = countingBlock.render({
        count,
      });
      countingBlock.set('components', block);
    }, 1000);

    return () => {
      clearInterval(countInterval);
    };
  }, []);

  return (
    <div>
      <div id="gjs"></div>
    </div>
  );
};

export default CounterNumberBlock;
